function [ext] = ExtractRegion(img)[sx, sy] =size(img);x_center_shift_factor = 190y_center_shift_factor = 180x_center = (sx / 2) + x_center_shift_factor;y_center = (sy / 2) + y_center_shift_factor;h_offset = 725;v_offset = 725;min_x = x_center - h_offset;min_y = y_center - v_offset;width = h_offset * 2;height = v_offset * 2;ext = imcrop(img, [min_x, min_y, width, height]);figureimshow(uint8(ext));end