function [filled_image] = FillImage(img)  close all;  [sx, sy] =size(img);  b_step1 = img > 50;  step1 = bwmorph(b_step1, 'erode');  step2 = ApplyMask(img, step1);  window_size = [ 5 5 ];  step3 = medfilt2(step2);  b_step3 = step3 > 50;  step4 = bwmorph(b_step3, 'thin' );  step5 = ApplyMask(img, step4);  step6 = medfilt2(step5, [21 21]);  b_step7 = step6 > 50;  step8 = imfill(b_step7, 'holes');  filled_image = step8;end