function [ctr_hist, ttf_hist] = PerformIrisAnalysis( control_images,                                         treated_images,                                        shrink_value,                                        threshold,                                        height_bins,                                        intensity_bins,                                        trial_number,                                        write_polar_images  )                                            % control_images  %   an array of images belonging to the control group  % treated_images  %   an array of images belonging to the treated group  % shrink_value  %   the downsampling factor when converting from cartesian to polar  % threshold  %   the intensity value to threshold the polar image by  % height_bins  %   the number of spatial partitions performed on the polar image    % intensity_bins  %   the number of intensity partitions performed on the polar   %   image        % trial_number  %   the 'position in the stack' of images       % write_polar_images  %   flag indicating whether or not we write out the polar transformed image  %   (default will write out to the same folder but with _polar.tiff ext)                                        control = uint8(PolarImageTransform(imread(control_images), shrink_value));ttf = uint8(PolarImageTransform(imread(treated_images), shrink_value));if (write_polar_images == 1)  start_ctr_str = '06-19-2017/tiffs/polar/ctr';    start_ttf_str = '06-19-2017/tiffs/polar/ttf';    if(trial_number ~= -1)    index_str = num2str(trial_number);  end        end_str = "_polar.tiff";    control_string = strcat(strcat(start_ctr_str, index_str), end_str);  ttf_string = strcat(strcat(start_ttf_str, index_str), end_str);    imwrite(control, control_string);  imwrite(ttf, ttf_string);endctr_hist = BandedHistogram(ThresholdImage(control, threshold), height_bins, intensity_bins)';ttf_hist = BandedHistogram(ThresholdImage(ttf, threshold), height_bins, intensity_bins)';end