function PlotIrisData( control,                       treated,                       fig_number)%                       line_type  )                                    [sx1, ~] = size(control);  v1=[];   v2=[];      for i=1:sx1    v1 = [v1; control(i,2);];    v2 = [v2; treated(i,2);];  end  %plot(v1, line_type)  figure(fig_number),  hold on  plot(v1, 'r-')  plot(v2, 'b-')  hold off%  title ("Control Vs. Treated", "fontsize", 20);  h = legend ({"control"}, "treated");  legend (h, "location", "northeast");  set (h, "fontsize", 10);%  grid on  xlabel ("Time");  ylabel ("Number of Cells");   start_str = '06-19-2017/figures';  end_str = '.jpeg';  figure_string = strcat(strcat(start_str, num2str(fig_number)), end_str);  print (figure_string)  end  